mapper.select:
select 

from organization t
where t.id = #{id,jdbcType=BIGINT}

mapper.insert:
insert into organization (id, org_name, org_number, 
  state, create_time, update_time, 
  operator, org_prov, org_addr
  )
values (#{id,jdbcType=BIGINT}, #{orgName,jdbcType=VARCHAR}, #{orgNumber,jdbcType=VARCHAR}, 
  #{state,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{operator,jdbcType=BIGINT}, #{orgProv,jdbcType=VARCHAR}, #{orgAddr,jdbcType=VARCHAR}
  )
